import React, { useState } from "react";
import "App.scss";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Home } from "components/display/Home";
import { Login } from "components/display/Login";
import { Confirmation } from "components/display/ConfirmEmail";
import Saved from "components/display/SavedUsers";
import { Sidebar, Segment, Menu, Icon } from "semantic-ui-react";

const UserContext = React.createContext<string | undefined>(undefined);

function App() {
  const [visible, setVisibility] = useState(false);

  if (visible) {
    return (
      <Router>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation="overlay"
            icon="labeled"
            inverted
            onHide={() => setVisibility(false)}
            vertical
            visible={visible}
            width="thin"
          >
            <Link to="/">
              <Menu.Item as="a">
                <Icon name="home" />
                Home
              </Menu.Item>
            </Link>
            <Link to="/test-user">
              <Menu.Item as="a">
                <Icon name="folder" />
                Test - Saved
              </Menu.Item>
            </Link>
            <Link to="/stage-user">
              <Menu.Item as="a">
                <Icon
                  name="folder outline"
                  onMouseEnter={(data: any) => console.log(data)}
                  onMouseLeave={() => console.log("leave")}
                />
                Stage - Saved
              </Menu.Item>
            </Link>
            <Link to="/login">
              <Menu.Item as="a">
                <Icon name="log out" />
                Log Out
              </Menu.Item>
            </Link>
          </Sidebar>
          <Sidebar.Pusher dimmed={visible}>
            <div className="App">
              <UserContext.Provider value="berk">
                <Switch>
                  <Route path="/confirm">
                    <Confirmation />
                  </Route>
                  <Route path="/login">
                    <Login />
                  </Route>
                  <Route path="/test-user">
                    <Saved env="test" />
                  </Route>
                  <Route path="/">
                    <Home />
                  </Route>
                </Switch>
              </UserContext.Provider>
            </div>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
        <div
          style={{
            position: "absolute",
            left: "0",
            top: "0",
            width: "5%",
            height: "100%",
          }}
          onMouseEnter={() => setVisibility(true)}
        />
        <div
          style={{
            position: "absolute",
            left: "20%",
            top: "0",
            width: "25%",
            height: "100%",
          }}
          onMouseOver={() => setVisibility(false)}
        />
      </Router>
    );
  }
}

export default App;
