import React, { useState } from "react";
import UserForm from "./components/Landing";
import SidebarComponent from "components/Sidebar";
import "App.scss";
import { Button } from "semantic-ui-react";
function App() {
  const [visible, setVisibility] = useState(false);
  return (
    <div className="App">
      <SidebarComponent visible={visible} setVisibility={setVisibility}>
        <UserForm />
      </SidebarComponent>
      <Button onClick={() => setVisibility(!visible)}>Show Menu</Button>
    </div>
  );
}

export default App;
