import React, { useState } from "react";
import { Button, Checkbox, Icon, Label } from "semantic-ui-react";
import { getSavedUsers, saveUser } from "api/db-init";

const Submit: React.FC = () => {
  const [checked, setCheck] = useState<boolean | undefined>(false);
  return (
    <React.Fragment>
      <div style={{ display: "flex", alignItems: "middle" }}>
        <span style={checked ? { color: "magenta" } : null}>Password</span>
        <Checkbox toggle onChange={(e, data) => setCheck(data.checked)} />
      </div>
      <Button
        icon
        labelPosition="left"
        id="saveUser"
        size="medium"
        color="instagram"
        type="button"
        style={{
          display: "inline-block",
          margin: "10px auto 10px 5px",
        }}
        onClick={(e) => console.log(getSavedUsers())}
      >
        <Icon name="plus" />
        Save User
      </Button>
    </React.Fragment>
  );
};

export default Submit;
