import React, { useState } from "react";
import "App.scss";
import { Router, Switch, Route, Link, Redirect } from "react-router-dom";
import { Login } from "components/display/Login";
import { Confirmation } from "components/display/ConfirmEmail";
import Saved from "components/display/SavedUsers";
import { Sidebar, Segment, Menu, Icon, SemanticICONS } from "semantic-ui-react";
import Landing from "components/display/Landing";
import { UserContext } from "store/ContextApi/UserContext";
import { createBrowserHistory } from "history";
import { client } from "api/db-actions";

export const history = createBrowserHistory();

function App() {
  const [visible, setVisibility] = useState(false);
  const [isLogged, setLogged] = useState<object>({
    logged: false,
    email: "",
    password: "",
  });
  let logIcon: SemanticICONS = client.auth.isLoggedIn
    ? "sign in alternate"
    : "log out";

  return (
    <Router history={history}>
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          onMouseLeave={() => setVisibility(false)}
          as={Menu}
          animation="overlay"
          icon="labeled"
          inverted
          onHide={() => setVisibility(false)}
          vertical
          visible={visible}
          width="thin"
        >
          <Link to="/">
            <Menu.Item as="a">
              <Icon name="home" />
              Home
            </Menu.Item>
          </Link>
          <Link to="/test-user">
            <Menu.Item as="a">
              <Icon name="folder" />
              Test - Saved
            </Menu.Item>
          </Link>
          <Link to="/stage-user">
            <Menu.Item as="a">
              <Icon name="folder outline" />
              Stage - Saved
            </Menu.Item>
          </Link>
          <Link to="/login">
            <Menu.Item as="a" onClick={() => client.auth.logout()}>
              <Icon name={logIcon} />
              Log Out
            </Menu.Item>
          </Link>
        </Sidebar>
        <Sidebar.Pusher dimmed={visible}>
          <div className="App">
            <UserContext.Provider
              value={{
                loggedIn: client.auth.isLoggedIn,
                logOut: client.auth.logout,
              }}
            >
              <Switch>
                <Route path="/confirm">
                  <Confirmation />
                </Route>
                <Route path="/login">
                  <Login />
                </Route>
                <Route path="/test-user">
                  <Saved env="test" />
                </Route>
                <Route path="/">
                  <Landing />
                </Route>
                {!client.auth.isLoggedIn && <Redirect push to="/login" />}
              </Switch>
            </UserContext.Provider>
          </div>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
      <div
        style={{
          position: "absolute",
          left: "0",
          top: "0",
          width: "5%",
          height: "100%",
        }}
        onMouseEnter={() => setVisibility(true)}
      />
    </Router>
  );
}

export default App;
