import React, { useEffect, useContext, useState } from "react";
import { Grid, Dimmer, Loader, Button } from "semantic-ui-react";
import Statistics from "components/container/statistics";
import { LoginWithGoogle } from "core/initApp";
import { UserContext } from "core/store/ContextApi/Context";
import { history } from "App";

export const Login: React.FC<any> = () => {
  // eslint-disable-next-line
  let User = useContext(UserContext);
  const [dimmer, setDimmer] = useState(false);
  const handleLogin = () => {
    setDimmer(true);
    LoginWithGoogle(User.actions.setAuthState).finally(() => {
      setDimmer(false);
    });
  };
  useEffect(() => {
    if (User.isLoggedIn) {
      history.push("/");
    }
    // eslint-disable-next-line
  }, [User.isLoggedIn]);

  return (
    <React.Fragment>
      <Dimmer active={false}>
        <Loader />
      </Dimmer>

      <Grid columns={1} relaxed="very" stackable>
        <Grid.Column>
          <Button onClick={handleLogin}>Login</Button>
        </Grid.Column>
      </Grid>

      <Statistics />
    </React.Fragment>
  );
};

export default Login;
