import React from "react";
import { Popup, Button, Icon } from "semantic-ui-react";

function copyToClipboard(str: string) {
  const el = document.createElement("textarea");
  el.value = str;
  document.body.appendChild(el);
  el.select();
  document.execCommand("copy");
  document.body.removeChild(el);
}
const handleClick = (
  event: React.MouseEvent<HTMLButtonElement, MouseEvent>
) => {
  if (event.currentTarget.firstElementChild) {
    copyToClipboard(event.currentTarget.firstElementChild.innerHTML);
  }
};

const triggerButton = (name: string) => (
  <Button
    type="button"
    style={{ width: "125px" }}
    animated="fade"
    size="medium"
    onClick={(e) => handleClick(e)}
  >
    <Button.Content visible>{name}</Button.Content>
    <Button.Content hidden>
      Copy <Icon style={{ marginLeft: "4px" }} name="clipboard" />
    </Button.Content>
  </Button>
);

const ClipBoardButton: React.FC<{ name: string }> = ({ name }) => {
  return (
    <div style={{ margin: "5px" }}>
      <Popup
        trigger={triggerButton(name)}
        content={`Copied to clipboard.`}
        on="click"
        position="right center"
      />
    </div>
  );
};

export default ClipBoardButton;
