import React from "react";
import WithCard from "components/container/withCard";
import { fetchUsers } from "api/db-actions";
import { Button } from "semantic-ui-react";

type Props = {
  env: string;
};

export default class Saved extends React.Component<Props> {
  state = {
    users: ["berk", "akgün"],
  };

  async componentDidMount() {
    const users = await fetchUsers(this.props.env);
    this.setState({ users });
  }
  render() {
    return (
      <div>
        {this.state.users ? (
          this.state.users.map((id) => <WithCard key={id} />)
        ) : (
          <React.Fragment>
            <h1>There is no saved user</h1>
            <Button color="green">Go to Home</Button>
          </React.Fragment>
        )}
      </div>
    );
  }
}
