import React from "react";
import { Card, Image, Flag } from "semantic-ui-react";
import { CardElement } from "assets/propTypes";
import ClipBoardButton from "components/container/withClipboard";

const images = require.context("images", true);

const CustomUser = (props: CardElement) => {
  return (
    <Card centered>
      <Image
        circular
        src={images("./" + (props.country ? props.country : "tr") + ".png")}
        wrapped
        ui={false}
        label={
          props.environment !== ""
            ? {
                as: "a",
                color: props.environment === "Test" ? "red" : "blue",
                content: props.environment === "Test" ? "TEST" : "STAGE",
                ribbon: true,
              }
            : null
        }
      ></Image>
      <Card.Content>
        <Card.Header>
          <span>Serbest Meslek ErbabÄ±</span>
        </Card.Header>
        <Card.Meta>
          ShapeShifter
          <span>
            <Flag
              style={{ marginLeft: "4px" }}
              name={props.country}
              size="huge"
            />
          </span>
        </Card.Meta>
        <Card.Description>
          <div> Valar Morghulis</div>
        </Card.Description>
      </Card.Content>
      <Card.Content extra textAlign="center">
        {userId ? <ClipBoardButton name="Id" copy={userId} /> : null}
        {email ? <ClipBoardButton name="Email" copy={email} /> : null}
        {password ? <ClipBoardButton name="Password" copy={password} /> : null}
        {accessToken ? (
          <ClipBoardButton name="Token" copy={accessToken} />
        ) : null}
      </Card.Content>
    </Card>
  );
};

export default CustomUser;
