type AppState = {
  userId: string;
  active: boolean;
};
type Action =
  | { type: "userId"; payload: string }
  | { type: "active"; payload: boolean };

export const InitialState = {
  userId: "",
  active: false,
  error: "",
  modal: false,
};

export function reducer(state: AppState, action: Action): AppState {
  switch (action.type) {
    case "userId":
      return { ...state, userId: action.payload };
    case "active":
      return { ...state, active: true };
    default:
      return state;
  }
}
