import { UserElement } from "../../assets/propTypes";
import { USER } from '../actionTypes';

const initialState: UserElement = {
  job: false,
  country: 'tr',
  service: 156,
  quoteNumber: 2,
  deal: false
};

type action = {
  type: string,
  payload: any
}


export default function (state = initialState, action: action) {
  switch (action.type) {
    case USER.DEAL: {

      return {
        ...state,
        deal: action.payload
      }
    }
    default:
      return state
  }
}
