import React, { useRef } from "react";
import { Popup, Button, Icon } from "semantic-ui-react";
import CopyToClipboard from "react-copy-to-clipboard";

const TriggerButton: React.FC<{
  name: string;
  copy: string;
}> = ({ name, copy }) => {
  const ref = useRef(null);
  return (
    <CopyToClipboard text={copy}>
      <Button
        ref={ref}
        basic
        type="button"
        style={{ width: "125px" }}
        animated="fade"
        size="medium"
      >
        <Button.Content visible>{name}</Button.Content>
        <Button.Content hidden>
          Copy <Icon style={{ marginLeft: "4px" }} name="clipboard" />
        </Button.Content>
      </Button>
    </CopyToClipboard>
  );
};

const ClipBoardButton: React.FC<{ name: string; copy: string }> = ({
  name,
  copy,
}) => {
  return (
    <div style={{ margin: "5px" }}>
      <Popup
        on="click"
        trigger={<TriggerButton name={name} copy={copy} />}
        content={`Copied to clipboard.`}
        pinned
        position="right center"
      />
    </div>
  );
};

export default ClipBoardButton;
