import {
  Stitch,
  RemoteMongoClient,
  AnonymousCredential,
  UserPasswordCredential,
} from "mongodb-stitch-browser-sdk";

const client = Stitch.initializeDefaultAppClient("ttk-ui-cvkwt");

const db = client
  .getServiceClient(RemoteMongoClient.factory, "mongodb-atlas")
  .db("ttk-ui");

export async function fetchUsers(environment) {
  return client.auth.loginWithCredential(new AnonymousCredential()).then(() => {
    db.collection(environment).find().asArray();
  });
}

export function Login(email, password) {
  const credential = new UserPasswordCredential(email, password);
  client.auth.loginWithCredential(credential);
}

export function saveUser(environment) {
  client.auth.loginWithCredential(new AnonymousCredential()).then(() => {
    db.collection(environment)
      .insertOne({
        auth: "asdnaksjdnasjdhasd",
        email: "berkakgun@email.com",
        password: "123456789",
      })
      .then((result) =>
        console.log(`Successfully inserted item with _id: ${result.insertedId}`)
      )
      .catch((err) => console.error(`Failed to insert item: ${err}`));
  });
}
