import React from "react";
import { Card, Image } from "semantic-ui-react";
import pokemon from "images/pokemon-destek.png";

export default class DefaultUser extends React.Component {
  render() {
    return (
      <React.Fragment>
        <Card centered>
          <Image
            src={pokemon}
            wrapped
            ui={false}
            label={
              props.environment !== ""
                ? {
                    as: "a",
                    color: props.environment === "Test" ? "red" : "blue",
                    content: props.environment === "Test" ? "TEST" : "STAGE",
                    ribbon: true,
                  }
                : null
            }
          />
          <Card.Content>
            <Card.Header>Pokemon Destek</Card.Header>
            <Card.Meta>
              <span>Ash Ketchum</span>
            </Card.Meta>
            <Card.Description>
              I wanna be the very best like no one ever was
            </Card.Description>
          </Card.Content>
          <Card.Content extra textAlign="center">
            Berk
          </Card.Content>
        </Card>
      </React.Fragment>
    );
  }
}
