import React from "react";
import { Switch, Route, Redirect } from "react-router";
import Login from "./Auth/Login";
import { Confirmation } from "./Auth/ConfirmEmail";
import Redirection from "./Auth/Redirect";
import Saved from "./SavedUsers";
import Landing from "./Landing";
import { useStitchAuth } from "components/AuthGuard";

const Routes: React.FC = () => {
  const { isLoggedIn } = useStitchAuth();
  return (
    <div className="App">
      <Switch>
        <Route path="/login" component={Login} />
        <Route path="/confirm" component={Confirmation} />
        <Route path="/redirect" component={Redirection} />
        {!isLoggedIn && <Redirect push to="/login" />}

        <Route path="/test-user">
          <Saved env="Test" />
        </Route>
        <Route path="/stage-user">
          <Saved env="Stage" />
        </Route>

        <Route path="/" component={Landing} />
      </Switch>
    </div>
  );
};

export default Routes;
