import React from "react";
import { Link } from "react-router-dom";
import { Menu, Icon } from "semantic-ui-react";
import { useStitchAuth } from "components/AuthGuard";

const SideMenu: React.FC = () => {
  let {
    isLoggedIn,
    actions: { handleLogout },
  } = useStitchAuth();

  return (
    <React.Fragment>
      <Link to="/">
        <Menu.Item as="div">
          <Icon name="home" />
          Home
        </Menu.Item>
      </Link>
      <Link to="/test-user">
        <Menu.Item as="div">
          <Icon name="folder" />
          Test - Saved
        </Menu.Item>
      </Link>
      <Link to="/stage-user">
        <Menu.Item as="div">
          <Icon name="folder outline" />
          Stage - Saved
        </Menu.Item>
      </Link>
      <Link to="/login">
        <Menu.Item
          as="div"
          onClick={() => {
            if (isLoggedIn) handleLogout();
          }}
        >
          <Icon name={!isLoggedIn ? "sign-in" : "sign-out"} />
          {!isLoggedIn ? "Log In" : "Log Out"}
        </Menu.Item>
      </Link>
    </React.Fragment>
  );
};

export default SideMenu;
