import React, { useState } from "react";
import { Statistic } from "semantic-ui-react";
import fetch from "node-fetch";

async function getStats() {
  const url =
    "https://webhooks.mongodb-stitch.com/api/client/v2.0/app/ttk-ui-cvkwt/service/userCount/incoming_webhook/userCountWebHook";
  const { views, users, members } = await fetch(url, {
    method: "GET",
    body: "",
  }).then((res) => res.json());
}

const initialItems = [
  { key: "create", label: "Users", value: "22" },
  { key: "views", label: "Views", value: "31,200" },
  { key: "members", label: "Members", value: "22" },
];

const Statistics = () => {
  const [items, setItems] = useState(initialItems);

  return (
    <div style={{ position: "absolute", bottom: "15%" }}>
      <Statistic.Group items={items} />
    </div>
  );
};

export default Statistics;
