import React from "react";
import { Statistic } from "semantic-ui-react";
import { db } from "api/db-actions";

const pipeline = {
  $group: {
    _id: "$userId",
  },
};
db.collection("test")
  .aggregate([pipeline])
  .toArray()
  .then((categories) => {
    console.log(
      `Successfully grouped users for ${categories.length} customers.`
    );
  });

const items = [
  { key: "create", label: "Users", value: "22" },
  { key: "views", label: "Views", value: "31,200" },
  { key: "members", label: "Members", value: "22" },
];

const Statistics = () => (
  <div style={{ position: "absolute", bottom: "15%" }}>
    <Statistic.Group items={items} />
  </div>
);

export default Statistics;
