import React, { useState } from "react";
import "App.scss";
import { Router, Switch, Route, Link, Redirect } from "react-router-dom";
import { Login } from "components/display/Login";
import { Confirmation } from "components/display/ConfirmEmail";
import Saved from "components/display/SavedUsers";
import { Sidebar, Segment, Menu, Icon } from "semantic-ui-react";
import Landing from "components/display/Landing";
import { createBrowserHistory } from "history";
import { AuthGuard, useStitchAuth } from "components/AuthGuard";

import Redirection from "components/display/Redirect";
import SideMenu from "components/display/SideMenu";

export const history = createBrowserHistory();

function App() {
  const [visible, setVisibility] = useState(false);
  let {
    isLoggedIn,
    actions: { handleLogout },
  } = useStitchAuth();

  return (
    <Router history={history}>
      <AuthGuard>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            onMouseLeave={() => setVisibility(false)}
            as={Menu}
            animation="overlay"
            icon="labeled"
            inverted
            onHide={() => setVisibility(false)}
            vertical
            visible={visible}
            width="thin"
          >
            <SideMenu />
          </Sidebar>
          <Sidebar.Pusher dimmed={visible}>
            <div className="App">
              <Switch>
                <Route path="/login">
                  <Login />
                </Route>

                <Route path="/confirm">
                  <Confirmation />
                </Route>
                <Route path="/redirect" component={Redirection} />
                {!isLoggedIn && <Redirect push to="/login" />}
                <Route path="/test-user">
                  <Saved env="Test" />
                </Route>
                <Route path="/stage-user">
                  <Saved env="Stage" />
                </Route>
                <Route path="/">
                  <Landing />
                </Route>
              </Switch>
            </div>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
        <div
          style={{
            position: "absolute",
            left: "0",
            top: "0",
            width: "3%",
            height: "100%",
          }}
          onMouseEnter={() => setVisibility(true)}
        />
      </AuthGuard>
    </Router>
  );
}

export default App;
