import React, { useState } from "react";
import "App.scss";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useHistory,
  Link,
} from "react-router-dom";
import { Home } from "components/Home";
import { Login } from "components/Login";
import { Confirmation } from "components/ConfirmEmail";
import Saved from "components/container/WithSaved";
import { Sidebar, Segment, Menu, Icon, Button } from "semantic-ui-react";

const SidebarContext = React.createContext<boolean | undefined>(undefined);

function App() {
  const [visible, setVisibility] = useState(false);
  let history = useHistory();

  const handleClick = (url: string) => history.push(url);
  return (
    <SidebarContext.Provider value={true}>
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          as={Menu}
          animation="overlay"
          icon="labeled"
          inverted
          onHide={() => setVisibility(false)}
          vertical
          visible={visible}
          width="thin"
        >
          <Menu.Item as="a">
            <Link to="/">
              <Icon name="home" />
              Home
            </Link>
          </Menu.Item>
          <Menu.Item as="a" onClick={() => handleClick("/test-user")}>
            <Icon name="folder" />
            Test - Saved
          </Menu.Item>
          <Menu.Item as="a">
            <Icon
              name="folder outline"
              onMouseEnter={(data: any) => console.log(data)}
              onMouseLeave={() => console.log("leave")}
            />
            Stage - Saved
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="log out" />
            Log Out
          </Menu.Item>
        </Sidebar>
        <Sidebar.Pusher dimmed={visible}>
          <div className="App">
            <Router>
              <Switch>
                <Route path="/confirm">
                  <Confirmation />
                </Route>
                <Route path="/login">
                  <Login />
                </Route>
                <Route path="/test-user">
                  <Saved env="test" />
                </Route>
                <Route path="/">
                  <Home />
                </Route>
              </Switch>
            </Router>
          </div>
          <Button
            style={{
              position: "absolute",
              top: "10%",
              right: "10%",
              transform: "rotate(270deg)",
            }}
            onClick={() => setVisibility(!visible)}
          >
            Show Sidebar
          </Button>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    </SidebarContext.Provider>
  );
}

export default App;
