import React from "react";
import { Message } from "semantic-ui-react";

const WithMessage: React.FC<{ message: string; header?: string }> = ({
  message,
  header,
}) => (
  <Message>
    <Message.Header>{header} </Message.Header>
    <p>{message} </p>
  </Message>
);

export default WithMessage;
