import React from "react";
import { Grid, Container } from "semantic-ui-react";
import CustomUser from "../cards/customUser";
import UserForm from "components/forms/userForm";
import { connect, useSelector } from "react-redux";
import Header from "./Header";
import { getUserElements } from "store/selectors";
import DefaultUser from "../cards/defaultUser";
import Submit from "components/display/Submit";
import { db } from "api/db-actions";

const pipeline = {
  $group: {
    _id: "$userId",
  },
};

async function stats() {
  const test = await db
    .collection("test")
    .aggregate([pipeline])
    .toArray()
    .then((categories) => categories.length);
  const stage = await db
    .collection("stage")
    .aggregate([pipeline])
    .toArray()
    .then((categories) => {
      return categories.length;
    });
  console.log(test + stage);
}
stats();
const Landing = () => {
  const user = useSelector(getUserElements);
  return (
    <React.Fragment>
      <div style={{ margin: "0px 15%" }}>
        <Grid columns={user.type === "Custom" ? 2 : 1} divided stackable>
          <Grid.Column>
            <Header />
            {user.type === "Custom" ? (
              <CustomUser {...user} />
            ) : (
              <DefaultUser />
            )}
          </Grid.Column>
          {user.type === "Custom" ? (
            <React.Fragment>
              <Grid.Column>
                <UserForm />
              </Grid.Column>
            </React.Fragment>
          ) : null}
          <Container style={{ margin: "15px auto" }}>
            <Submit />
          </Container>
        </Grid>
      </div>
    </React.Fragment>
  );
};

export default connect(null, null)(Landing);
