import * as Realm from "realm-web";

import { mongodb } from "./db/constants";
import { redirect_uri, client_id } from "assets/constants";

export const app = new Realm.App({ id: "ttk-ui-cvkwt" });

export const mongo = app.services.mongodb(mongodb.service);

export const collection = mongo
  .db(mongodb.database)
  .collection(mongodb.collection);

/*
 * Create form to request access token from Google's OAuth 2.0 server.
 */
export function getGoogleAccessToken() {
  // Google's OAuth 2.0 endpoint for requesting an access token
  var oauth2Endpoint = "https://accounts.google.com/o/oauth2/v2/auth";

  // Create <form> element to submit parameters to OAuth 2.0 endpoint.
  var form = document.createElement("form");
  form.setAttribute("method", "GET"); // Send as a GET request.
  form.setAttribute("action", oauth2Endpoint);

  // Parameters to pass to OAuth 2.0 endpoint.
  var params = {
    client_id: client_id,
    redirect_uri: redirect_uri,
    response_type: "token",
    scope: "https://www.googleapis.com/auth/drive.metadata.readonly",
    include_granted_scopes: "true",
  };

  // Add form parameters as hidden input values.
  for (var p in params) {
    var input = document.createElement("input");
    input.setAttribute("type", "hidden");
    input.setAttribute("name", p);
    input.setAttribute("value", params[p]);
    form.appendChild(input);
  }

  // Add form to page and submit it to open the OAuth 2.0 endpoint.
  document.body.appendChild(form);
  form.submit();
}

export function LoginWithGoogle(accessToken) {
  const credentials = Realm.Credentials.google(accessToken);
  app.logIn(credentials).then((user) => {
    console.log(`Logged in with id: ${user.id}`);
  });
}

export async function SignUp(email, password) {
  try {
    await app.emailPasswordAuth.registerUser(email, password);
    return { success: true, error: undefined };
  } catch (error) {
    console.log(error);
    return { success: false, error };
  }
}

export async function ConfirmUser() {
  const url = window.location.search;
  const params = new URLSearchParams(url);
  const token = params.get("token");
  const tokenId = params.get("tokenId");

  return app.emailPasswordAuth.confirmUser(token, tokenId);
}

export function SendResetPasswordEmail(email) {
  return app.emailPasswordAuth
    .sendResetPasswordEmail(email)
    .then(() => {
      console.log("Successfully sent password reset email!");
    })
    .catch((err) => {
      console.log("Error sending password reset email:", err);
    });
}
