import React, { useState } from "react";
import UserForm from "./components/Landing";
import SidebarComponent from "components/Sidebar";
import "App.scss";
import { Button, Sidebar, Segment, Menu, Icon } from "semantic-ui-react";
function App() {
  const [visible, setVisibility] = useState(false);
  return (
    <div className="App">
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          as={Menu}
          animation="overlay"
          icon="labeled"
          inverted
          onHide={() => props.setVisibility(false)}
          vertical
          visible={props.visible}
          width="thin"
        >
          <Menu.Item as="a">
            <Icon name="home" />
            Home
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="gamepad" />
            Games
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="camera" />
            Channels
          </Menu.Item>
        </Sidebar>

        <Sidebar.Pusher>
          <Segment basic>
            <UserForm />
          </Segment>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    </div>
  );
}

export default App;
