import React, { useState } from "react";
import { Grid, Dropdown } from "semantic-ui-react";
import { dispatchSelectable } from "store/actions";
import { connect } from "react-redux";
import { getUserElements } from "store/selectors";

type PropTypes = { name: string; options: any; dispatchSelectable: any };

const WithSelect = (props: PropTypes) => {
  const [select, handleSelect] = useState();
  const handleChange = (value: string) => {
    handleSelect(value);
    dispatchSelectable("COUNTRY", value);
  };
  return (
    <React.Fragment>
      <Grid.Column textAlign="center" className="selectable">
        <Dropdown
          selection
          clearable
          placeholder={`Select ${props.name}`}
          options={props.options}
          onChange={(e, data) => handleSelect(data.value)}
        />
      </Grid.Column>
    </React.Fragment>
  );
};

export default connect(null, { dispatchSelectable })(WithSelect);
