import React, { useState } from "react";
import UserForm from "./components/Landing";
import "App.scss";
import { Button, Sidebar, Segment, Menu, Icon } from "semantic-ui-react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
function App() {
  const [visible, setVisibility] = useState(false);
  return (
    <div className="App">
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          as={Menu}
          animation="overlay"
          icon="labeled"
          inverted
          onHide={() => setVisibility(false)}
          vertical
          visible={visible}
          width="thin"
        >
          <Menu.Item as="a">
            <Icon name="home" />
            Home
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="play" />
            Test - Saved
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="camera" />
            Stage - Saved
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="log out" />
            Log Out
          </Menu.Item>
        </Sidebar>

        <Sidebar.Pusher>
          <Segment basic>
            <UserForm />
            <Button onClick={() => setVisibility(!visible)}>
              Show Sidebar
            </Button>
          </Segment>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    </div>
  );
}

export default App;
