import React, { createRef } from "react";
import { Button, Icon, Popup } from "semantic-ui-react";
import CopyToClipboard from "react-copy-to-clipboard";

const ClipBoardButton: React.FC<{ name: string; copy: string }> = ({
  name,
  copy,
}) => {
  let buttonRef = createRef();
  return (
    <div style={{ margin: "5px" }}>
      <CopyToClipboard text={copy}>
        <Button
          basic
          type="button"
          style={{ width: "125px" }}
          animated="fade"
          size="medium"
        >
          <Button.Content visible>{name}</Button.Content>
          <Popup content="copied to your clipboard" triggerRef={buttonRef}>
            <Button.Content hidden ref={buttonRef}>
              Copy <Icon style={{ marginLeft: "4px" }} name="clipboard" />
            </Button.Content>
          </Popup>
        </Button>
      </CopyToClipboard>
    </div>
  );
};

export default ClipBoardButton;
