import {
  Stitch,
  RemoteMongoClient,
  AnonymousCredential,
  UserPasswordCredential,
  UserPasswordAuthProviderClient,
} from "mongodb-stitch-browser-sdk";

const client = Stitch.initializeDefaultAppClient("ttk-ui-cvkwt");

const db = client
  .getServiceClient(RemoteMongoClient.factory, "mongodb-atlas")
  .db("ttk-ui");

export async function fetchUsers(environment) {
  return client.auth.loginWithCredential(new AnonymousCredential()).then(() => {
    db.collection(environment).find().asArray();
  });
}

export async function logDB(email, password) {
  const credential = new UserPasswordCredential(email, password);
  try {
    const authedUser = await client.auth.loginWithCredential(credential);
    console.log(`successfully logged in with id: ${authedUser.id}`);
    return { success: true, id: authedUser.id, error: undefined };
  } catch (err) {
    console.error(`login failed with error: ${err}`);
    return { success: false, error: err };
  }
}

export function saveUser(environment) {
  client.auth.loginWithCredential(new AnonymousCredential()).then(() => {
    db.collection(environment)
      .insertOne({
        auth: "asdnaksjdnasjdhasd",
        email: "berkakgun@email.com",
        password: "123456789",
      })
      .then((result) =>
        console.log(`Successfully inserted item with _id: ${result.insertedId}`)
      )
      .catch((err) => console.error(`Failed to insert item: ${err}`));
  });
}

export async function SignUp(email, password) {
  const emailPasswordClient = Stitch.defaultAppClient.auth.getProviderClient(
    UserPasswordAuthProviderClient.factory
  );

  try {
    await emailPasswordClient.registerWithEmail(email, password);
    return { success: true, error: undefined };
  } catch (err) {
    console.log(err);
    return { success: false, error: err };
  }
}

export async function confirmUser() {
  const url = window.location.search;
  const params = new URLSearchParams(url);
  const token = params.get("token");
  const tokenId = params.get("tokenId");
  console.log(token, tokenId);

  // Confirm the user's email/password account
  const emailPassClient = Stitch.defaultAppClient.auth.getProviderClient(
    UserPasswordAuthProviderClient.factory
  );

  console.log(emailPassClient);

  return emailPassClient
    .confirmUser(token, tokenId)
    .then((res) => console.log(res));
}

export function resendConfirmation(email) {
  const emailPassClient = Stitch.defaultAppClient.auth.getProviderClient(
    UserPasswordAuthProviderClient.factory
  );

  emailPassClient
    .sendResetPasswordEmail(email)
    .then(() => {
      console.log("Successfully sent password reset email!");
    })
    .catch((err) => {
      console.log("Error sending password reset email:", err);
    });
}
