import React from "react";
import { confirmUser } from "api/db-actions";

export const Confirmation: React.FC = () => {
  const confirmed = async () => await confirmUser();
  const component = confirmed ? (
    <div>Your email has been confirmed</div>
  ) : (
    <div>Berk</div>
  );
  return component;
};
