import React, { useState } from "react";
import { Button, Checkbox, Icon, Label } from "semantic-ui-react";
import { fetchUsers } from "api/db-actions";
import { CreateDefaultUser } from "./hooks/createDefaultUser";
import { useSelector } from "react-redux";
import { getUserElements } from "store/selectors";

const Submit: React.FC = () => {
  const user = useSelector(getUserElements);
  const [checked, setCheck] = useState<boolean | undefined>(false);
  const [isLoading, load] = CreateDefaultUser(user.environment);
  return (
    <React.Fragment>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Label
          className="wrapper"
          style={checked ? { color: "#2185d0" } : undefined}
        >
          Phone <Icon style={{ marginRight: "5px" }} name="mobile" />
          <Checkbox onChange={(e, data) => setCheck(data.checked)} />
        </Label>
      </div>
      <Button
        icon
        labelPosition="left"
        id="saveUser"
        size="medium"
        color="instagram"
        type="button"
        style={{
          display: "inline-block",
          margin: "10px auto 10px 5px",
        }}
        onClick={(e) => console.log(fetchUsers())}
      >
        <Icon name="plus" />
        Create User
      </Button>
    </React.Fragment>
  );
};

export default Submit;
