import React, { useState, useReducer } from "react";
import {
  Segment,
  Grid,
  Form,
  Button,
  Divider,
  Dimmer,
  Loader,
} from "semantic-ui-react";
import SignUp from "api/db-signup";
import { useHistory } from "react-router";
import WithMessage from "./container/withMessage";
import Statistics from "components/container/statistics";

function loginReducer(state: any, action: any) {
  switch (action.type) {
    case "increment":
      return { count: state.count + 1 };
    case "decrement":
      return { count: state.count - 1 };
    default:
      throw new Error();
  }
}

export const Login: React.FC = () => {
  const initialState = {
    email: "",
    password: "",
    active: false,
    error: false,
  };
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [state, dispatch] = useReducer(loginReducer, initialState);
  const [active, setActive] = useState(false);
  const [error, setError] = useState(false);
  const history = useHistory();
  const handleLogin = async () => {
    setActive(true);
    SignUp(email, password).then((res) =>
      res ? history.push("/") : setError(true)
    );
  };
  const handleSignup = async () => {
    setActive(true);
    SignUp(email, password).then((res) =>
      res ? history.push("/") : setError(true)
    );
  };
  return (
    <React.Fragment>
      <div className="logo">
        <img
          alt="armut-logo"
          title="armut-logo"
          src="https://cdn.armut.com/images/tr:h-70/armut-header-logo-colour@2x.png"
        />
      </div>
      <WithMessage
        message={
          !error
            ? "Log in or Sign up so that we can save your important users"
            : "Error"
        }
        header="Welcome to Armut Test Tool Kit"
      />

      <Segment placeholder>
        <Dimmer active={active}>
          <Loader indeterminate>Let You In</Loader>
        </Dimmer>
        <Grid columns={2} relaxed="very" stackable>
          <Grid.Column>
            <Form>
              <Form.Input
                icon="user"
                iconPosition="left"
                label="Username"
                placeholder="Username"
                onChange={(e, data) => setEmail(data.value)}
              />
              <Form.Input
                icon="lock"
                iconPosition="left"
                label="Password"
                type="password"
                placeholder="Password"
                onChange={(e, data) => setPassword(data.value)}
              />
            </Form>
            <Button
              content="Login"
              icon="sign in"
              type="submit"
              primary
              style={{ marginTop: "10px" }}
              onClick={handleLogin}
            />
          </Grid.Column>

          <Grid.Column verticalAlign="middle">
            <Form>
              <Form.Input
                icon="user"
                iconPosition="left"
                label="Username"
                placeholder="Username"
                onChange={(e, data) => setEmail(data.value)}
              />
              <Form.Input
                icon="lock"
                iconPosition="left"
                label="Password"
                type="password"
                placeholder="Password"
                onChange={(e, data) => setPassword(data.value)}
              />
            </Form>

            <Button
              content="Sign up"
              icon="signup"
              type="submit"
              color="vk"
              style={{ marginTop: "10px" }}
              onClick={handleSignup}
            />
          </Grid.Column>
        </Grid>

        <Divider vertical>Or</Divider>
      </Segment>
      <Statistics />
    </React.Fragment>
  );
};

export default Login;
