import React, { useContext, useState } from "react";
import { UserContext } from "core/store/ContextApi/Context";
import { app } from "core/db";

export function useStitchAuth() {
  const context = useContext(UserContext);
  console.log(context);
  if (!context) {
    return { isLoggedIn: false, actions: {} };
  }
  return context;
}

export function AuthGuard(props: any) {
  const [authState, setAuthState] = useState({
    isLoggedIn: app.currentUser?.state === "active",
    currentUser: app.currentUser,
  });

  const handleLogout = () => app.currentUser?.logOut;
  const authInfo = React.useMemo(() => {
    const { isLoggedIn, currentUser } = authState;
    const value = {
      isLoggedIn,
      currentUser,
      actions: { handleLogout, setAuthState },
    };
    return value;
  }, [authState]);
  return (
    <UserContext.Provider value={authInfo}>
      {props.children}
    </UserContext.Provider>
  );
}
