import React, { useState } from "react";
import { Grid, Button } from "semantic-ui-react";
import { connect } from "react-redux";

const WithButtonGroup = ({ left, right }: { left: string; right: string }) => {
  let [deal, setDeal] = useState(0);
  return (
    <React.Fragment>
      <Grid.Column textAlign="center" className="selectable">
        <Button.Group size="medium" style={{ marginTop: "30px" }}>
          with
          <Button
            type="button"
            positive={deal === 0}
            onClick={() => setDeal(0)}
          >
            {left}
          </Button>
          />
          <Button.Or />
          <Button
            type="button"
            positive={deal === 1}
            onClick={() => setDeal(1)}
          >
            {right}
          </Button>
        </Button.Group>
      </Grid.Column>
    </React.Fragment>
  );
};

const mapPropsToState = (selection) => {
  return {
    deal: selection,
  };
};

export default connect(mapPropsToState, WithButtonGroup);
