import React, { useState } from "react";
import { confirmUser } from "api/db-actions";
import { Button, Form } from "semantic-ui-react";
import { useHistory } from "react-router";
import WithMessage from "components/container/withMessage";

export const Confirmation: React.FC = () => {
  const [error, setError] = useState(false);
  const [mail, setMail] = useState("");
  const history = useHistory();
  try {
    confirmUser()
      .then((res) => history.push("/login"))
      .catch(() => setError(true));
  } catch (error) {
    setError(true);
  }
  return (
    <React.Fragment>
      {error ? (
        <React.Fragment>
          <WithMessage
            header="Please try to login with your email and password"
            message="If you cant login it means you had a trouble with confirmation of your email."
          />
          <Form.Input
            icon="user"
            iconPosition="left"
            type="email"
            placeholder="Email"
            onChange={(e, data) => console.log(data)}
          />
          <Button style={{ marginLeft: "10px" }}>
            Resend Confirmation Mail
          </Button>
        </React.Fragment>
      ) : null}
    </React.Fragment>
  );
};
