import React, { useState, useReducer } from "react";
import {
  Segment,
  Grid,
  Form,
  Button,
  Divider,
  Dimmer,
  Loader,
} from "semantic-ui-react";
import SignUp from "api/db-signup";
import { login } from "api/db-init";
import { useHistory } from "react-router";
import WithMessage from "./container/withMessage";
import Statistics from "components/container/statistics";

type AppState = {
  email: string;
  password: string;
  active: boolean;
  error: string;
  logged: boolean;
};
type Action =
  | { type: "login"; payload: string }
  | { type: "signup"; payload: number }
  | { type: "email"; payload: string }
  | { type: "password"; payload: string }
  | { type: "error"; payload: string };

export function reducer(state: AppState, action: Action): AppState {
  switch (action.type) {
    case "email":
      return {
        ...state,
        email: action.payload,
      };
    case "password":
      return {
        ...state,
        password: action.payload,
      };
    case "error":
      return {
        ...state,
        error: action.payload,
        active: false,
      };
    case "login":
      return {
        ...state,
      };
    case "signup":
      return {
        ...state,
      };
    default:
      return state;
  }
}

export const Login: React.FC = () => {
  const initialState = {
    email: "",
    password: "",
    active: false,
    error: "",
    logged: false,
  };
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [state, dispatch] = useReducer(reducer, initialState);
  const [active, setActive] = useState(false);
  const [error, setError] = useState(false);
  const history = useHistory();
  const handleLogin = async () => {
    setActive(true);
    login(email, password).then((res) =>
      res.success
        ? history.push("/")
        : dispatch({ type: "error", payload: res.error })
    );
  };
  const handleSignup = async () => {
    setActive(true);
    SignUp(email, password).then((res) =>
      res.success
        ? history.push("/")
        : dispatch({ type: "error", payload: res.error })
    );
  };
  return (
    <React.Fragment>
      <div className="logo">
        <img
          alt="armut-logo"
          title="armut-logo"
          src="https://cdn.armut.com/images/tr:h-70/armut-header-logo-colour@2x.png"
        />
      </div>
      <WithMessage
        message={
          !state.error
            ? "Log in or Sign up so that we can save your important users"
            : state.error
        }
        header={
          !state.error
            ? "Welcome to Armut Test Tool Kit"
            : "Somethings Wrong..."
        }
      />

      <Segment placeholder>
        <Dimmer active={active}>
          <Loader indeterminate>Let You In</Loader>
        </Dimmer>
        <Grid columns={2} relaxed="very" stackable>
          <Grid.Column>
            <Form>
              <Form.Input
                icon="user"
                iconPosition="left"
                label="Username"
                placeholder="Username"
                onChange={(e, data) => setEmail(data.value)}
              />
              <Form.Input
                icon="lock"
                iconPosition="left"
                label="Password"
                type="password"
                placeholder="Password"
                onChange={(e, data) => setPassword(data.value)}
              />
            </Form>
            <Button
              content="Login"
              icon="sign in"
              type="submit"
              primary
              style={{ marginTop: "10px" }}
              onClick={handleLogin}
            />
          </Grid.Column>

          <Grid.Column verticalAlign="middle">
            <Form>
              <Form.Input
                icon="user"
                iconPosition="left"
                label="Username"
                placeholder="Username"
                onChange={(e, data) => setEmail(data.value)}
              />
              <Form.Input
                icon="lock"
                iconPosition="left"
                label="Password"
                type="password"
                placeholder="Password"
                onChange={(e, data) => setPassword(data.value)}
              />
            </Form>

            <Button
              content="Sign up"
              icon="signup"
              type="submit"
              color="vk"
              style={{ marginTop: "10px" }}
              onClick={handleSignup}
            />
          </Grid.Column>
        </Grid>

        <Divider vertical>Or</Divider>
      </Segment>
      <Statistics />
    </React.Fragment>
  );
};

export default Login;
