import React, { useState } from "react";
import { Statistic } from "semantic-ui-react";
import fetch from "node-fetch";

const items = [
  { key: "1", label: "Users", value: "1" },
  { key: "2", label: "Views", value: "1" },
  { key: "3", label: "Members", value: "1" },
];

async function getStats(): SemanticShorthandCollection<StatisticProps> {
  const url =
    "https://webhooks.mongodb-stitch.com/api/client/v2.0/app/ttk-ui-cvkwt/service/userCount/incoming_webhook/statsWebHook";
  const stats = await fetch(url, {
    method: "GET",
  }).then((res) => res.json());

  items.forEach((el, index) => {
    el.value = stats[index];
  });
  return items;
}

const Statistics = () => {
  const [items, setItems] = useState(async () => await getStats());
  return (
    <div style={{ position: "absolute", bottom: "15%" }}>
      <Statistic.Group items={items} />
    </div>
  );
};

export default Statistics;
