import React, { useState } from "react";
import { confirmUser } from "api/db-actions";
import Login from "./Login";

export const Confirmation: React.FC = () => {
  const [error, setError] = useState(false);
  try {
    confirmUser();
  } catch (error) {}

  return <Login></Login>;
};
