import React from "react";
import { Grid, Label, Select } from "semantic-ui-react";

const WithSelect = ({ name, options }: { name: string; options: any }) => {
  return (
    <React.Fragment className="selectable">
      <Grid.Column textAlign="center">
        <Label>{name.toUpperCase()} </Label>
        <Select placeholder={`Select ${name}`} options={options} />
      </Grid.Column>
    </React.Fragment>
  );
};

export default WithSelect;
