import React, { useState } from "react";
import { Segment, Grid, Form, Button, Divider } from "semantic-ui-react";
import SignUp from "api/db-signup";

export const Login: React.FC = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleSubmit = (email, password) => SignUp(email, password);
  return (
    <Segment placeholder>
      <Grid columns={2} relaxed="very" stackable>
        <Grid.Column>
          <Form onSubmit={handleSubmit}>
            <Form.Input
              icon="user"
              iconPosition="left"
              label="Username"
              placeholder="Username"
              onChange={(e, data) => setEmail(data.value)}
            />
            <Form.Input
              icon="lock"
              iconPosition="left"
              label="Password"
              type="password"
              onChange={(e, data) => setPassword(data.value)}
            />

            <Button content="Login" primary type="submit" />
          </Form>
        </Grid.Column>

        <Grid.Column verticalAlign="middle">
          <Button content="Sign up" icon="signup" size="big" />
        </Grid.Column>
      </Grid>

      <Divider vertical>Or</Divider>
    </Segment>
  );
};

export default Login;
