import React from "react";
import { Card, Image } from "semantic-ui-react";
import pokemon from "images/pokemon-destek.png";
import { useSelector } from "react-redux";
import { getUserElements } from "store/selectors";

const DefaultUser: React.FC = () => {
  const user = useSelector(getUserElements);
  const card = useSelector(getCardElements);
  return (
    <React.Fragment>
      <Card centered>
        <Image
          src={pokemon}
          wrapped
          ui={false}
          label={
            user.environment !== ""
              ? {
                  as: "a",
                  color: user.environment === "Test" ? "red" : "blue",
                  content: user.environment === "Test" ? "TEST" : "STAGE",
                  ribbon: true,
                }
              : null
          }
        />
        <Card.Content>
          <Card.Header>Pokemon Destek</Card.Header>
          <Card.Meta>
            <span>Ash Ketchum</span>
          </Card.Meta>
          <Card.Description>
            I wanna be the very best like no one ever was
          </Card.Description>
        </Card.Content>
        <Card.Content extra textAlign="center">
          Berk
        </Card.Content>
      </Card>
    </React.Fragment>
  );
};

export default DefaultUser;
