import React, { useEffect, useState } from "react";
import { Label, Grid, Button, Popup } from "semantic-ui-react";
import DefaultUser from "../cards/defaultUser";
import CustomUserCard from "../cards/customUser";

const CustomUser = (props) => {
  let values = props.values;
  let passProps = props.handleForm;
  let card = props.options;

  useEffect(() => {
    values.deal = deal;
    passProps(values);
  });
  let [deal, setDeal] = useState(0);
  return (
    <React.Fragment>
      <div style={{ margin: "0px 15%" }}>
        <Grid columns={2} divided stackable>
          <Grid.Column>
            <CustomUserCard flag={values.country} card={card} />
          </Grid.Column>
          <Grid.Column>
            <Grid columns={4}>
              <Grid.Row centered>
                <Grid.Column textAlign="center">
                  <Label>Country</Label>
                  <Field
                    name="country"
                    component="select"
                    className="countrySelect"
                    style={{ marginTop: "12px" }}
                  >
                    <option value="tr">Turkish</option>
                    <option value="ro">Romanian</option>
                    <option value="sa">Arabic</option>
                    <option value="eg">Egyptian</option>
                    <option value="us">American</option>
                    <option value="uk">British</option>
                  </Field>
                </Grid.Column>
                <Grid.Column textAlign="center">
                  <span style={{ marginBottom: "5px 0px 0px" }}>
                    <Label size="medium">
                      Password
                      <Field
                        name="password"
                        type="checkbox"
                        checked={values.password}
                        style={{ marginLeft: "4px" }}
                      />
                    </Label>
                  </span>
                </Grid.Column>
                <Grid.Column textAlign="center">
                  <Label size="medium" style={{ margin: " 0px" }}>
                    with Job
                    <Field
                      name="job"
                      type="checkbox"
                      checked={values.job}
                      style={{ marginLeft: "4px" }}
                    />
                  </Label>
                  {values.job ? (
                    <div>
                      <Field
                        name="service"
                        component="select"
                        style={{ margin: "12px 0px 0px " }}
                      >
                        <option value="1">Pokemon</option>
                        <option value="142">Nakliye</option>
                        <option value="32">Badana</option>
                        <option value="31">İlaçlama</option>
                        <option value="156">Psikolog</option>
                        <option value="64">Özel Ders</option>
                      </Field>
                    </div>
                  ) : null}
                </Grid.Column>
                <Grid.Column textAlign="center" style={{ width: "auto" }}>
                  {values.job === true ? (
                    <React.Fragment>
                      <Button.Group size="medium" style={{ marginTop: "30px" }}>
                        with
                        <Popup
                          content="Creates 2 quotes for you"
                          pinned
                          position="bottom center"
                          on="hover"
                          trigger={
                            <Button
                              type="button"
                              positive={deal === 0}
                              onClick={() => setDeal(0)}
                            >
                              Quote
                            </Button>
                          }
                        />
                        <Button.Or />
                        <Button
                          type="button"
                          positive={deal === 1}
                          onClick={() => setDeal(1)}
                        >
                          Deal
                        </Button>
                      </Button.Group>
                    </React.Fragment>
                  ) : null}
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Grid.Column>
        </Grid>
      </div>
    </React.Fragment>
  );
};

const CustomUserFormik = withFormik({
  mapPropsToValues({ password, job, country, service, quoteNumber, deal }) {
    return {
      password: password || true,
      job: job || false,
      country: country || "tr",
      service: service || 156,
      quoteNumber: quoteNumber || 2,
      deal: deal,
    };
  },
})(CustomUser);

export default CustomUserFormik;

export default UserForm;
