import React, { useReducer, useEffect, useContext } from "react";
import { Grid, Dimmer, Loader, Button } from "semantic-ui-react";
import Statistics from "components/container/statistics";
import { reducer } from "components/hooks/loginReducer";
import { LoginWithGoogle } from "core/db";
import { UserContext } from "core/store/ContextApi/Context";
import { useHistory } from "react-router";

export const Login: React.FC<any> = (props: any) => {
  const initialState = {
    email: "",
    password: "",
    active: false,
    error: "",
    modal: false,
  };

  // eslint-disable-next-line
  const [state, dispatch] = useReducer(reducer, initialState);
  let { isLoggedIn } = useContext(UserContext);
  let history = useHistory();
  useEffect(() => {
    if (isLoggedIn) history.push("/");
  }, isLoggedIn);

  return (
    <React.Fragment>
      <Dimmer active={state.active}>
        <Loader />
      </Dimmer>

      <Grid columns={1} relaxed="very" stackable>
        <Grid.Column>
          <Button onClick={() => LoginWithGoogle()}>Login</Button>
        </Grid.Column>
      </Grid>

      <Statistics />
    </React.Fragment>
  );
};

export default Login;
