import * as Realm from "realm-web";

import { mongodb } from "./db/constants";

export const app = new Realm.App({ id: "ttk-ui-cvkwt" });

export const mongo = app.services.mongodb(mongodb.service);

export const collection = mongo
  .db(mongodb.database)
  .collection(mongodb.collection);

export async function SignUp(email, password) {
  try {
    await app.emailPasswordAuth.registerUser(email, password);
    return { success: true, error: undefined };
  } catch (error) {
    console.log(error);
    return { success: false, error };
  }
}

export async function LogIn() {
  const redirect_uri = "https://ttk-ui-cvkwt.mongodbstitch.com/";
  const client_id =
    "291216037477-tlo72q1hnsl5kk6qph3spkc191mhnfn2.apps.googleusercontent.com";
  const url = `https://accounts.google.com/o/oauth2/v2/auth?
  scope=https%3A//www.googleapis.com/auth/drive.metadata.readonly&
  include_granted_scopes=true&
  response_type=token&
  redirect_uri=${redirect_uri}&
  client_id=${client_id}`;
  try {
    Realm.Credentials.google();
  } catch (err) {
    console.log(err);
  }
}

export async function ConfirmUser() {
  const url = window.location.search;
  const params = new URLSearchParams(url);
  const token = params.get("token");
  const tokenId = params.get("tokenId");

  return app.emailPasswordAuth.confirmUser(token, tokenId);
}

export function SendResetPasswordEmail(email) {
  return app.emailPasswordAuth
    .sendResetPasswordEmail(email)
    .then(() => {
      console.log("Successfully sent password reset email!");
    })
    .catch((err) => {
      console.log("Error sending password reset email:", err);
    });
}
