import React, { useState } from "react";
import { Sidebar, Segment, Menu, Icon, Button } from "semantic-ui-react";
import { Link } from "react-router-dom";
import Landing from "./Landing";
import { SignUp } from "./forms/SignUp";

export const Home = () => {
  const [visible, setVisibility] = useState(false);
  return (
    <React.Fragment>
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          as={Menu}
          animation="overlay"
          icon="labeled"
          inverted
          onHide={() => setVisibility(false)}
          vertical
          visible={visible}
          width="thin"
        >
          <Menu.Item as="a">
            <Icon name="home" />
            <Link to="/sign-up">Home</Link>
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="play" />
            Test - Saved
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="camera" />
            Stage - Saved
          </Menu.Item>
          <Menu.Item as="a">
            <Icon name="log out" />
            Log Out
          </Menu.Item>
        </Sidebar>

        <Sidebar.Pusher>
          <Segment basic>
            <Landing />
            <Button onClick={() => setVisibility(!visible)}>
              Show Sidebar
            </Button>
          </Segment>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    </React.Fragment>
  );
};
