import React, { useState } from "react";
import "App.scss";
import { Router, Switch, Route, Redirect } from "react-router-dom";
import { Login } from "components/display/Login";
import { Confirmation } from "components/display/ConfirmEmail";
import Saved from "components/display/SavedUsers";
import { Sidebar, Segment, Menu } from "semantic-ui-react";
import Landing from "components/display/Landing";
import { createBrowserHistory } from "history";
import { AuthGuard, useStitchAuth } from "components/AuthGuard";

import Redirection from "components/display/Redirect";
import SideMenu from "components/display/SideMenu";

export const history = createBrowserHistory();

function App() {
  const [visible, setVisibility] = useState(false);
  let { isLoggedIn } = useStitchAuth();

  return (
    <Router history={history}>
      <AuthGuard>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            onMouseLeave={() => setVisibility(false)}
            as={Menu}
            animation="overlay"
            icon="labeled"
            inverted
            onHide={() => setVisibility(false)}
            vertical
            visible={visible}
            width="thin"
          >
            <SideMenu />
          </Sidebar>
          <Sidebar.Pusher dimmed={visible}>
            <Routes />
          </Sidebar.Pusher>
        </Sidebar.Pushable>
        <div
          style={{
            position: "absolute",
            left: "0",
            top: "0",
            width: "3%",
            height: "100%",
          }}
          onMouseEnter={() => setVisibility(true)}
        />
      </AuthGuard>
    </Router>
  );
}

export default App;
