import React, { useState } from "react";
import { Segment, Grid, Form, Button, Divider } from "semantic-ui-react";
import SignUp from "api/db-signup";
import { useHistory } from "react-router";
import WithMessage from "./container/withMessage";

export const Login: React.FC = () => {
  const FormFC = () => {
    return (
      <Form onSubmit={handleSubmit}>
        <Form.Input
          icon="user"
          iconPosition="left"
          label="Username"
          placeholder="Username"
          onChange={(e, data) => setEmail(data.value)}
        />
        <Form.Input
          icon="lock"
          iconPosition="left"
          label="Password"
          type="password"
          onChange={(e, data) => setPassword(data.value)}
        />
      </Form>
    );
  };
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();
  const handleSubmit = async () => {
    SignUp(email, password).then((res) =>
      res ? history.push("/") : history.push("/error")
    );
  };
  return (
    <React.Fragment>
      <WithMessage
        message="Log in or Sign up so that we can save your important users"
        header="Welcome Armut Test Tool Kit"
      />

      <Segment placeholder>
        <Grid columns={2} relaxed="very" stackable>
          <Grid.Column>
            <FormFC />
            <Button
              content="Login"
              icon="sign in"
              type="submit"
              primary
              style={{ marginTop: "10px" }}
            />
          </Grid.Column>

          <Grid.Column verticalAlign="middle">
            <FormFC />
            <Button
              content="Sign up"
              icon="signup"
              type="submit"
              color="vk"
              style={{ marginTop: "10px" }}
            />
          </Grid.Column>
        </Grid>

        <Divider vertical>Or</Divider>
      </Segment>
    </React.Fragment>
  );
};

export default Login;
