import React from "react";
import { Grid, Form, Input } from "semantic-ui-react";
import {
  countryOptions,
  businessModelOptions,
  jobOptionsBM2,
  jobOptionsBM1,
} from "assets/selectableOptions";
import "react-dates/initialize";
import {
  DateRangePicker,
  SingleDatePicker,
  DayPickerRangeController,
} from "react-dates";
import "react-dates/lib/css/_datepicker.css";
import WithSelect from "components/container/withSelect";
import WithButtonGroup from "components/container/withButtonGroup";
import { useSelector } from "react-redux";
import { getUserElements } from "store/selectors";

const UserForm: React.FC = () => {
  const user = useSelector(getUserElements);
  console.log(user.bm);
  return (
    <React.Fragment>
      <Form>
        <Grid columns={2} divided="vertically">
          <Grid.Row centered>
            <WithSelect name="Country" options={countryOptions} />
          </Grid.Row>
          <Grid.Row centered>
            <Grid.Column>
              <WithSelect
                name="Business Model"
                options={businessModelOptions}
              />
              <WithSelect
                name="Job"
                options={user.bm === "1" ? jobOptionsBM1 : jobOptionsBM2}
              />
            </Grid.Column>
            <Grid.Column>
              <WithButtonGroup left="Quote" right="Deal" />
              <SingleDatePicker />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Form>
    </React.Fragment>
  );
};

export default UserForm;
