import React, { useEffect, useRef } from "react";
import { Button, Icon, Dimmer, Loader } from "semantic-ui-react";
import { CreateDefaultUser } from "../hooks/createDefaultUser";
import { useSelector } from "react-redux";
import { getUserElements } from "store/selectors";
import { CreateCustomUser } from "components/hooks/createCustomUser";

const Submit: React.FC = () => {
  const user = useSelector(getUserElements);
  let ref = useRef(user.type);
  let type;
  useEffect(() => {
    type = ref.current;
  }, [user.type]);
  const [isLoading, loadUser] =
    type === "Default"
      ? CreateDefaultUser(user.environment)
      : CreateCustomUser(user.environment);

  console.log(user.type);
  console.log(user);
  return (
    <React.Fragment>
      <Dimmer active={isLoading}>
        <Loader />
      </Dimmer>
      <Button
        icon
        labelPosition="left"
        id="saveUser"
        size="medium"
        color="instagram"
        type="button"
        style={{
          display: "inline-block",
          margin: "10px auto 10px 5px",
        }}
        onClick={() => loadUser()}
      >
        <Icon name="plus" />
        Create User
      </Button>
    </React.Fragment>
  );
};

export default Submit;
