import React, { useState } from "react";
import { Grid, Button } from "semantic-ui-react";
import { connect } from "react-redux";
import { dispatchDeal } from "../../store/actions";

type PropTypes = { left: string; right: string; dispatchDeal: any };

const WithButtonGroup: React.FC<PropTypes> = (props) => {
  let [deal, setDeal] = useState<number>(0);

  console.log(props);

  const handleClick = (click: number): void => {
    if (deal === 0) setDeal(1);
    else setDeal(0);
    props.dispatchDeal(deal);
  };

  return (
    <React.Fragment>
      <Grid.Column textAlign="center" className="selectable">
        <Button.Group size="medium" style={{ marginTop: "30px" }}>
          with
          <Button type="button" positive={deal === 0} onClick={handleClick(1)}>
            {props.left}
          </Button>
          />
          <Button.Or />
          <Button type="button" positive={deal === 1} onClick={handleClick}>
            {props.right}
          </Button>
        </Button.Group>
      </Grid.Column>
    </React.Fragment>
  );
};

export default connect(null, { dispatchDeal })(WithButtonGroup);
//export default WithButtonGroup;
