import { Button, Checkbox, Icon, Label, Dropdown } from "semantic-ui-react";
import { getSavedUsers } from "api/db-init";
import { countryOptions } from "assets/selectableOptions";

const Submit: React.FC = () => {
  state = { options };

  handleAddition = (e, { value }) => {
    this.setState((prevState) => ({
      options: [{ text: value, value }, ...prevState.options],
    }));
  };

  handleChange = (e, { value }) => this.setState({ currentValues: value });
  const [checked, setCheck] = useState<boolean | undefined>(false);
  return (
    <React.Fragment>
      <Dropdown
        options={countryOptions}
        placeholder="Choose Phone Options"
