import React from "react";
import WithCard from "components/container/withCard";
import { fetchUsers } from "api/db-actions";
import { Button, Dimmer, Loader } from "semantic-ui-react";
import { history } from "App";

type Props = {
  env: string;
};

export default class Saved extends React.Component<Props> {
  state = { users: [], loading: true };
  async componentDidMount() {
    const users = await fetchUsers(this.props.env);
    this.setState({ users });
  }
  render() {
    return (
      <div>
        {this.state.loading ? (
          <Dimmer active={this.state.loading}>
            <Loader />
          </Dimmer>
        ) : this.state.users.length ? (
          this.state.users.map((user, id) => <WithCard card={user} key={id} />)
        ) : (
          <React.Fragment>
            <h1>There is no saved user</h1>
            <Button color="green" onClick={() => history.push("/")}>
              Go to Create One
            </Button>
          </React.Fragment>
        )}
      </div>
    );
  }
}
